import { useState } from "react";
import Banner from "./Banner";
import Formulario from "./Formulario";
import Time from "./Time";


function App() {

  const [times, setTimes] = useState ([
    { id: 1,
      nome: "Dog",
      corPrimaria: "#82cffa",
      corSecundaria: "#e8f8ff"
    },
    { id: 2,
      nome: "Cat",
      corPrimaria: "#a6d157",
      corSecundaria: "#f0f8e2"
    },
    { id: 3,
      nome: "Rabbit",
      corPrimaria: "#e06b69",
      corSecundaria: "#fde7e8"
    },
    { id: 4,
      nome: "Pig",
      corPrimaria: "#ffba05",
      corSecundaria: "#fff5d9"
    },
    { id: 5,
      nome: "Duck",
      corPrimaria: "#57c278",
      corSecundaria: "#d9f7e9"
    },
  ]) 

  const inicial = [
    {
      nome: "Toddy",
      raca: "Vira lata",
      imagem: "data:image/jpeg;base64,",
      time: times[0].nome
    },
    {
      nome: "Bingo e Pipoca",
      raca: "Gatinhos",
      imagem: "data:image/jpeg;base64,",
      time: times[1].nome
    },
    {
      nome: "Orelhudo",
      raca: "Coelho",
      imagem: "data:image/jpeg;base64,",
      time: times[2].nome
    },
    {
      nome: "Rosado",
      raca: "Porquinho",
      imagem: "data:image/jpeg;base64,",
      time: times[3].nome
    },
    {
      nome: "Estrela",
      raca: "Patinho",
      imagem: "data:image/jpeg;base64,",
      time: times[4].nome
    }
  ]

  
  const [pets, setPets] = useState(inicial);
  console.log(pets)

  const aoNovoPetAdicionado = (pet) =>{
    // console.log(pet)
    setPets([...pets, pet])
    
  }

  function deletarPet(prop) {
    console.log("Pet deletado", prop)
  }

  function mudarCorDoTime (cor, nome){
    setTimes(times.map(time => {
      if(time.nome === nome){
        time.corPrimaria = cor;
      }
      return time;
    }))
  }

  function mudarCorSecundariaDoTime (cor, nome){
    setTimes(times.map(time => {
      if(time.nome === nome){
        time.corSecundaria = cor;
      }
      return time;
    }))
  }

  return (
    <>
      <Banner/>
      <Formulario times={times.map(time => time.nome)} aoPetCadastrado={aoNovoPetAdicionado}/>
      <h1 className="titulo">Cards dos meus bichinhos fofinhos</h1>
      {times.map(time => 
      <Time 
      key={time.nome} 
      nome={time.nome} 
      corPrimaria={time.corPrimaria} 
      corSecundaria={time.corSecundaria}
      pets={pets.filter (pet => pet.time === time.nome)}
      aoDeletar={deletarPet}
      mudarCor={mudarCorDoTime}
      mudarCorSecundaria={mudarCorSecundariaDoTime}
      />)}
    </>
  );
}

export default App;
